INSERT INTO `user_map_place` VALUES (458,'ted','台北全部電影院','yellow','true','光點臺北',80,NULL,121.522160900,25.053279000,NULL,'臺灣的外國領事館始於清末，當時在臺北、淡水、安平及打狗有英國及德國所設之領事館。日據時期，美國在臺北敕使街道（即今中山北路）設立一座領事館，這座有如白宮的洋樓一直延用至戰後，為臺灣與美國關係之歷史見證物，目前仍屹立在臺北市中山北路二段十八號，即目前我們所稱的三級古蹟「前美國駐臺北領事館」。這座外觀為白色的二層洋樓建築位於日據時期的御成町，當時這是由城內通往圓山神社的主要道路。依早期地圖判斷，這座領事館大約建於西元一九二五年前後，其建築平面略呈方形，入口朝北，但東側突出迥廊，可供遮蔭之用。廊柱為簡潔的希臘柱式，建築風接近於美國南方的殖民式樣，室內採中央走廊梯間佈局。「大使官邸」已由台積電文教基金會捐款修復完畢，以「台北之家」的名義於九十一年五月由文化局委託台灣電影文化協會經營管理，庭園區域設計為戶外咖啡座，屋內則有誠品書店與電影沙龍，另設有光點電影院，專門放映藝術電影。'),(459,'ted','台北全部電影院','yellow','true','in89豪華數位影城',2285,NULL,121.503975000,25.045479000,NULL,NULL),(460,'ted','台北全部電影院','yellow','true','喜滿客京華影城',2286,NULL,121.562430000,25.048494900,NULL,NULL),(461,'ted','台北全部電影院','yellow','true','欣欣秀泰影城',2287,NULL,121.525601300,25.054210300,NULL,NULL),(462,'ted','台北全部電影院','yellow','true','微風國賓影城',2288,NULL,121.544171700,25.046062900,NULL,NULL),(463,'ted','台北全部電影院','yellow','true','湳山戲院',2289,NULL,121.553818000,25.031574000,NULL,NULL),(464,'ted','台北全部電影院','yellow','true','臺北新光影城',2290,NULL,121.506392900,25.045293800,NULL,NULL),(465,'ted','台北全部電影院','yellow','true','景美佳佳戲院',2291,NULL,121.540653700,24.991841700,NULL,NULL),(466,'ted','台北全部電影院','yellow','true','新民生戲院',2292,NULL,121.564244000,25.058683000,NULL,NULL),(467,'ted','台北全部電影院','yellow','true','華威天母影城',2293,NULL,121.533953000,25.117233000,NULL,NULL),(468,'ted','台北全部電影院','yellow','true','日新威秀影城',2294,NULL,121.504408900,25.045405900,NULL,NULL),(469,'ted','台北全部電影院','yellow','true','陽明戲院',2295,NULL,121.526070600,25.088296800,NULL,NULL),(470,'ted','台北全部電影院','yellow','true','長春國賓影城',2296,NULL,121.534250900,25.054699800,NULL,NULL),(471,'ted','台北全部電影院','yellow','true','朝代大戲院',2297,NULL,121.516057300,25.062762600,NULL,NULL),(472,'ted','台北全部電影院','yellow','true','梅花數位影院',2298,NULL,121.549106700,25.024728100,NULL,NULL),(473,'ted','台北全部電影院','yellow','true','百老匯數位影城',2299,NULL,121.536633000,25.010707000,NULL,NULL),(474,'ted','台北全部電影院','yellow','true','國賓大戲院',2300,NULL,121.504646100,25.04298